mapper.delete:
DELETE FROM member_board
WHERE board_no = #{boardNo}
AND
board_user = #{boardUser}

mapper.select:
SELECT COUNT(*)
FROM member_board
WHERE board_no = #{boardNo}
AND board_user = #{boardUser}

mapper.insert:
		INSERT INTO member_board_comment(
            board_no,
            comment_user, 
            comment_content
        ) values(
            #{boardNo},
            #{commentUser},
            #{commentContent}
        )
